import React, { useState } from "react";

function SideDrawer() {
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [loading, setLoading] = useState(false);
  const [loadingChat, setLoadingChat] = useState();
  return (
    <>
      <Box d="flex" justifyContent="space-between" alignItems="center" bg="white"  w="100%" p="5px 10px 5px 10px">
        <Tooltip
          label="Search user to chat"
          hasArrow
          placement="bottom-end"
        >
          <Button variant="ghost"><i className="fas fa-search"></i>
          <Text d={{base:"none",md:"flex"}} px="4" >Search User</Text></Button>
        </Tooltip>
      </Box>
    </>
  );
}
export default SideDrawer;
