import { useHistory } from "react-router-dom";
import React, { createContext, useContext, useEffect, useState } from "react";

const ChatContext = createContext();

const ChatProvider = ({ children }) => {
  const [user, setUser] = useState(null); // Initialize to null for clarity
  const [selectedChat, setSelectedChat] = useState(null); // Initialize to null for clarity
  const [chats, setChats] = useState([]); // Initialize to an empty array
  const history = useHistory(); // useHistory for React Router v5

  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    setUser(userInfo);
    if (!userInfo) {
      history.push("/"); // Redirect to root if no user info
    }
  }, [history]);

  return (
    <ChatContext.Provider
      value={{ user, setUser, chats, setChats, selectedChat, setSelectedChat }}
    >
      {children}
    </ChatContext.Provider>
  );
};

export const useChatState = () => {
  return useContext(ChatContext);
};

export default ChatProvider;
